                padding: const EdgeInsets.all(8),
                child: StreamBuilder(
                    stream: _channel.stream,
                    builder: (context, snapshot) {
                      if (snapshot.hasData) {
                        print(snapshot);
                        final messageData = snapshot.data.toString();
                        final chatMessage = ChatMessage(
                          senderName: 'Dilkhush',
